// Clear previous errors
  variables['7d0e9db4-5c15-4b5a-8a8e-ec59911c152b'] = [];

  // Validate file
  if (!context.parameters['file'] || !context.parameters['file'].text) {
    variables['7d0e9db4-5c15-4b5a-8a8e-ec59911c152b'] = ['Envie um CSV.'];
    return;
  }

  // Parse CSV
  const text = context.parameters['file'].text;
  const lines = text.replace(/\r/g, '').split('\n').filter(x => x.trim());

  if (lines.length < 2) {
    variables['7d0e9db4-5c15-4b5a-8a8e-ec59911c152b'] = ['CSV sem dados.'];
    return;
  }

  // Get headers and validate required columns
  const headers = lines[0].split(',').map(h => h.trim().toLowerCase());
  const idx = (k) => headers.indexOf(k);
  const requiredColumns = ['pn', 'qtd'];

  for (const col of requiredColumns) {
    if (idx(col) === -1) {
      variables['7d0e9db4-5c15-4b5a-8a8e-ec59911c152b'].push(`CSV precisa da coluna: ${col}`);
    }
  }

  if (variables['7d0e9db4-5c15-4b5a-8a8e-ec59911c152b'].length > 0) {
    return;
  }

  // Parse data rows
  const equipamentos = [];

  for (let i = 1; i < lines.length; i++) {
    const cols = lines[i].split(',');
    if (!cols.length) continue;

    const row = (k) => {
      const j = idx(k);
      return j >= 0 ? cols[j].trim() : '';
    };

    const pn = (row('pn') || '').toUpperCase();
    const qtd = parseInt(row('qtd') || '0', 10);

    if (!pn || !(qtd > 0)) continue;

    equipamentos.push({
      item: parseInt(row('item') || String(equipamentos.length + 1), 10),
      pn,
      fabricante: row('fabricante') || '',
      qtd,
      descricao: row('descricao') || ''
    });
  }

  // Update state
  variables['04022df9-f151-4296-9e3b-aa8e63cf3076'] = equipamentos;
  variables['7bb0be75-2a09-4c19-8626-6f015acff0f3'].equipamentos_validos = equipamentos.length;

